mapper.update:
update member
set username = #{memberEditDto.username},
    password = #{memberEditDto.password}
where member_id = #{memberId}

mapper.select:
select signup_date, username, member_id, login_id, password
from member
where member_id = #{memberId}

select signup_date, username, member_id, login_id, password
from member
where login_id = #{loginId}
